mapper.select:
select 
	count(*) as cnt
from
	bookmark_tb
where 1=1
	and user_id = #{user_id}
	and place_id = #{place_id}
	and del_flag != 'Y'
	and sns_division = #{sns_division}

select 
	count(*) as cnt
from
	bookmark_tb
where 1=1
	and user_id = #{user_id}
	and id = #{id}
	and gubun = #{gubun}
	and del_flag != 'Y'
	and sns_division = #{sns_division}

mapper.delete:
delete
	from bookmark_tb
where
	1 = 1
	and place_id = #{place_id}
	and gubun = #{gubun}
	and user_id = #{user_id}
	and sns_division = #{sns_division}

delete
	from bookmark_tb
where
	1 = 1
	and place_id = #{place_id}
	and gubun = #{gubun}
	and user_id = #{user_id}
	and id = #{id}
	and sns_division = #{sns_division}

mapper.select:
select
	count(*) as count
from
	bookmark_tb
where
	1 = 1
	and id = #{review_id}
	and user_id = #{user_id}
	and sns_division = #{sns_division}
	and del_flag != 'Y'
