mapper.insert:
INSERT INTO portal_review_tb(
review_id,
place_id,
place_seq,
author,
portal,
del_flag,
write_time,
write_date,
add_date,
add_time,


		g_content,
		g_rating


		y_title,
		y_description,
		y_video_id,
		y_thumbnail_url,
		y_start_index


)
VALUES(
'PR' || nextval('portal_review_id')
, #{place_id}
, #{place_seq}
, #{author}
, #{portal}
, 'N'
, #{write_time}::time
, to_date(#{write_date},'yyyy-MM-dd')
, current_date
, current_time


		, TEXT(#{g_content})
		, #{g_rating}


		, #{y_title}
		, #{y_description}
		, #{y_video_id}
		, #{y_thumbnail_url}
		, #{y_start_index}


)

mapper.select:
SELECT
count(*) as cnt
FROM portal_review_tb
where 1 = 1
and place_id = #{place_id}
and portal = #{portal}


		and author = #{author}


		and y_video_id = #{y_video_id}


and del_flag != 'Y'

mapper.insert:
INSERT INTO 
