		add_date,
		add_time,
		add_user_id,
		update_date,
		update_time,
		update_user_id,
		del_flag
		)
	VALUES(
	#{attach_number}
	, #{attach_seq}
	, #{attach_name}
	, #{attach_route}
	, #{attach_extension}
	, #{attach_size}
	, #{review_user_id}
	, current_date
	, current_time
	, #{review_user_id}
	, current_date
	, current_time
	, #{review_user_id}
	, 'N'
	)

mapper.update:
update app_review_tb set review_contents = #{review_contents}, rating_point = #{rating_point}, update_date = current_date, update_time = current_time, update_user_id = #{review_user_id}
, attach_number = 'AT' || nextval('attachment_id')
, attach_number = null
where review_id = #{review_id};
